export type TemplateName = keyof typeof TEMPLATES;

export const AVAILABLE_TEMPLATES: readonly TemplateName[] = [
  'sequential-feynman',
] as const;

/**
 * Get template content by name
 * @throws Error if template not found
 */
export function getTemplate(name: string): string {
  const template = TEMPLATES[name as TemplateName];
  if (!template) {
    throw new Error(`Template not found: ${name}. Available templates: ${AVAILABLE_TEMPLATES.join(', ')}`);
  }
  return template;
}